        private void SeedEventTypes(BowTieContext db)
        {
            var path = Path.GetFullPath(Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "..\\JsonSeedData\\types.json"));
            var eventTypes = JsonConvert.DeserializeObject<List<EventType>>(File.ReadAllText(path));
            foreach (var type in eventTypes)
